import{_ as a,o as t,c as i,ai as r}from"./chunks/framework.DJocCLVL.js";const c=JSON.parse('{"title":"李白《客中作》读后感","description":"","frontmatter":{"title":"李白《客中作》读后感","prev":false,"next":false},"headers":[],"relativePath":"impression/shi/lb-kzz/index.md","filePath":"impression/shi/lb-kzz/index.md","lastUpdated":1738652935000}'),s={name:"impression/shi/lb-kzz/index.md"};function n(o,e,p,l,d,_){return t(),i("div",null,[...e[0]||(e[0]=[r('<h1 id="李白《客中作》读后感" tabindex="-1">李白《客中作》读后感 <a class="header-anchor" href="#李白《客中作》读后感" aria-label="Permalink to &quot;李白《客中作》读后感&quot;">​</a></h1><p>首先附上全文。</p><h2 id="全文" tabindex="-1">全文 <a class="header-anchor" href="#全文" aria-label="Permalink to &quot;全文&quot;">​</a></h2><p>兰陵美酒郁金香，玉碗盛来琥珀光。</p><p>但使主人能醉客，不知何处是他乡。</p><h2 id="读后感" tabindex="-1">读后感 <a class="header-anchor" href="#读后感" aria-label="Permalink to &quot;读后感&quot;">​</a></h2><p>一般诗词谈客居他乡，往往是表达作者的思乡之情，但李白的这首《客中作》初读却少有思乡之感。</p><p>“兰陵美酒郁金香”，李白客居兰陵之时，应是开元年间，入京之前。此时的诗人举着玉碗，喝着郁金香浸制的兰陵美酒，看着美酒如琥珀般光艳。这样的场景让爱酒的李白感到陶醉，满心愉悦。</p><p>“但使主人能醉客，不知何处是他乡”更印证了诗人此时的愉悦兴奋之情，颇有些乐不思蜀的味道。然而细细想来，即然已经提起了他乡，说明诗人并非不知道这里不是故乡。这又像是只因主人能醉客，才终究放下了自己的思乡之情，变得不知何处是他乡，也不知何处是故乡。</p><p>这首诗虽没写思乡，却又将思乡置于整首诗之前，最终与好友宴饮，不知何处是他乡，摆脱了思乡之情，多出了其他羁旅之作所没有的洒脱。李白的浪漫也由此可见一二。</p><p>当能专心做自己喜欢的事情时，烦恼也是很难追上的。</p>',11)])])}const m=a(s,[["render",n]]);export{c as __pageData,m as default};
