import{_ as a,c as t,o as s,ai as i}from"./chunks/framework.BKsRHAHS.js";const h=JSON.parse('{"title":"王昌龄《送柴侍御》读后感","description":"","frontmatter":{"title":"王昌龄《送柴侍御》读后感","prev":false,"next":false},"headers":[],"relativePath":"impression/shi/wcl-scsy/index.md","filePath":"impression/shi/wcl-scsy/index.md","lastUpdated":1738658069000}'),r={name:"impression/shi/wcl-scsy/index.md"};function n(o,e,p,l,d,c){return s(),t("div",null,[...e[0]||(e[0]=[i('<h1 id="王昌龄《送柴侍御》读后感" tabindex="-1">王昌龄《送柴侍御》读后感 <a class="header-anchor" href="#王昌龄《送柴侍御》读后感" aria-label="Permalink to &quot;王昌龄《送柴侍御》读后感&quot;">​</a></h1><p>首先附上全文。</p><h2 id="全文" tabindex="-1">全文 <a class="header-anchor" href="#全文" aria-label="Permalink to &quot;全文&quot;">​</a></h2><p>流水通波接武冈，送君不觉有离伤。</p><p>青山一道同云雨，明月何曾是两乡。</p><h2 id="读后感" tabindex="-1">读后感 <a class="header-anchor" href="#读后感" aria-label="Permalink to &quot;读后感&quot;">​</a></h2><p>古时友人别离，往往难测重逢之日。山高路远只能以诗词书信寄去思念之情。因此有“相见时难别亦难”，而王昌龄这首诗却不写难别，倒像是劝说难别的友人。</p><p>“流水通波接武冈”写的是友人此去的武冈，与诗人所居之地（推测应为龙标）往来交通便利，流水通波道路无阻。一个“接”字更显两地相近。颇有“朝辞白帝彩云间，千里江陵一日还。”之感。由此自然的“送君不觉有离伤”。即然如此的近，自然重聚或书信交流也不是难事，因此不要如此为离别而悲伤。</p><p>而“青山一道同云雨，明月何曾是两乡”写风雨相同，明月共睹，虽然相隔两地，却又不觉得是隔山隔水的两乡。这两句看似是宽慰友人不要因为相隔两地而悲伤，却又写出了诗人的友情之深。有此两句，往后若有风雨，诗人是否会想起青山另一边的友人；来日明月相照，诗人是否会有“但愿人长久，千里共婵娟”之感？俗语睹物思人，诗人以山川风雨，江河日月为物，在尚未别离之时，已对未来无法相见寄托愁思了。</p><p>道是无情却有情，诗人压低离愁别绪，宽慰友人，也是在宽慰自己。</p>',10)])])}const m=a(r,[["render",n]]);export{h as __pageData,m as default};
